import React, { Component } from 'react'
import img1 from "../img/poster1.jpg"
import img2 from "../img/poster2.jpg"
import img3 from "../img/poster3.jpg"
import img4 from "../img/poster4.jpg"

export default class Home extends Component {
    movies=[
        // {id:1 , name:"Tom and Jerry" , desc:"asdasdasdasdasdasdasdasdasd" , img1:img1},
        // {id:2 , name:"Tom and Jerry" , desc:"asdasdasdasdasdasdasdasdasd" , img1:img2},
        // {id:3 , name:"Tom and Jerry" , desc:"asdasdasdasdasdasdasdasdasd" , img1:img3},
        // {id:4 , name:"Tom and Jerry" , desc:"asdasdasdasdasdasdasdasdasd" , img1:img4},
        // {id:5 , name:"Tom and Jerry" , desc:"asdasdasdasdasdasdasdasdasd" , img1:img1},
        // {id:6 , name:"Tom and Jerry" , desc:"asdasdasdasdasdasdasdasdasd" , img1:img2},
    ]

    render() {
        return (
           <>
           <div className='container my-5'>
               <div className="row">
                   {this.movies.map((value, index)=>{
                       return (<div className="col-md-2">
                           <div className="item">
                               <img src={value.img1} className='w-100' alt="" />
                               <h2>{value.name}</h2>
                               <p>{value.desc}</p>
                           </div>
                           </div>
                       )
                   })}
               </div>
           </div>
           </>
        )
    }
}
