import React, { Component } from 'react'
import axios from 'axios'


export default class Home extends Component {
   
    
    state={movies:[]}
    getTrending= async ()=>{
      let {data}= await axios.get(`https://api.themoviedb.org/3/trending/tv/day?api_key=896e8f1d1bbca56d402bb80f5b1bae2c`)
   
      console.log(data.results);
      
      this.setState({
        movies:data.results
      })

    }
    componentDidMount(){
        this.getTrending()
    }

    render() {
        return (
           <>
             <div className="container my-5">
                    <div className="row">
                        <div className="col-md-4">
                            <div className="item"></div>
                            <div className="brdr w-25"></div>
                            <h1>Trending <br /> movies <br /> to watch now</h1>
                            <p className='secondFontColor'>Most watched Movies By Days</p>
                            <div className="brdr w-100"></div>
                        </div>
                       {this.state.movies.map((value , index)=>{
                           return(
                            <div key={index} className="col-md-2 my-3">
                            <div className="item">
                                <img className='w-100' src={'https://image.tmdb.org/t/p/original'+value.poster_path} alt="" />
                                <h3>{value.name}{value.title}</h3>
                                <p></p>
                            </div>
                        </div>
                           )

                       })}

                     </div>
                  </div>
           </>
        )
    }
}
